options.customCss:
:root {
  /* Vola Brand Colors */
  --chat--color-primary: #4169E1;  /* Vola Blue */
  --chat--color-primary-shade-50: #3B5FD9;
  --chat--color-primary-shade-100: #3555D1;
  --chat--color-secondary: #FFB800;  /* Vola Yellow/Orange */
  --chat--color-secondary-shade-50: #E6A500;
  --chat--color-white: #ffffff;
  --chat--color-light: #f8f9fd;
  --chat--color-light-shade-50: #eef1fb;
  --chat--color-light-shade-100: #d9dff7;
  --chat--color-medium: #c8d0e8;
  --chat--color-dark: #1a2040;
  --chat--color-disabled: #6b7280;
  --chat--color-typing: #374151;

  /* Base Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.5rem;  /* Slightly more rounded for modern look */
  --chat--transition-duration: 0.15s;

  /* Window Dimensions */
  --chat--window--width: 400px;
  --chat--window--height: 600px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid var(--chat--color-light-shade-50);
  --chat--window--border-radius: 1rem;  /* More rounded window */
  --chat--window--margin-bottom: var(--chat--spacing);

  /* Header Styles - Vola Blue */
  --chat--header-height: auto;
  --chat--header--padding: var(--chat--spacing);
  --chat--header--background: var(--chat--color-primary);
  --chat--header--color: var(--chat--color-white);
  --chat--header--border-top: none;
  --chat--header--border-bottom: none;
  --chat--header--border-left: none;
  --chat--header--border-right: none;
  --chat--heading--font-size: 2em;
  --chat--subtitle--font-size: inherit;
  --chat--subtitle--line-height: 1.8;

  /* Message Styles */
  --chat--message--font-size: 1rem;
  --chat--message--padding: var(--chat--spacing);
  --chat--message--border-radius: 1rem;  /* More rounded messages */
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: calc(var(--chat--spacing) * 1);
  --chat--message--bot--background: var(--chat--color-white);
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--bot--border: 1px solid var(--chat--color-light-shade-50);
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--user--border: none;
  --chat--message--pre--background: rgba(65, 105, 225, 0.1);  /* Light blue background for code */
  --chat--messages-list--padding: var(--chat--spacing);

  /* Toggle Button - Vola Blue with Yellow accent */
  --chat--toggle--size: 64px;
  --chat--toggle--width: var(--chat--toggle--size);
  --chat--toggle--height: var(--chat--toggle--size);
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);
  --chat--toggle--color: var(--chat--color-white);

  /* Button Styles */
  --chat--button--color: var(--chat--color-white);
  --chat--button--background: var(--chat--color-primary);
  --chat--button--padding: calc(var(--chat--spacing) * 1 / 2) var(--chat--spacing);
  --chat--button--border-radius: 0.5rem;
  --chat--button--hover--color: var(--chat--color-white);
  --chat--button--hover--background: var(--chat--color-primary-shade-50);
  --chat--close--button--color-hover: var(--chat--color-primary);

  /* Send and File Buttons - Yellow accent */
  --chat--input--send--button--background: var(--chat--color-secondary);
  --chat--input--send--button--color: var(--chat--color-dark);
  --chat--input--send--button--background-hover: var(--chat--color-secondary-shade-50);
  --chat--input--send--button--color-hover: var(--chat--color-dark);
  --chat--input--file--button--background: var(--chat--color-white);
  --chat--input--file--button--color: var(--chat--color-primary);
  --chat--input--file--button--background-hover: var(--chat--color-light);
  --chat--input--file--button--color-hover: var(--chat--color-primary-shade-50);
  --chat--files-spacing: 0.25rem;

  /* Body and Footer - Light blue tint */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-light);
  --chat--footer--color: var(--chat--color-dark);
}

/* Custom message styling for Vola theme */
.chat-message {
  max-width: 50%;
  box-shadow: 0 2px 8px rgba(65, 105, 225, 0.1);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.chat-message:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(65, 105, 225, 0.15);
}

/* Additional Vola-specific enhancements */
.chat-header {
  background: linear-gradient(135deg, var(--chat--color-primary) 0%, var(--chat--color-primary-shade-50) 100%);
}

.chat-toggle {
  box-shadow: 0 4px 16px rgba(65, 105, 225, 0.3);
  transition: all 0.3s ease;
}

.chat-toggle:hover {
  box-shadow: 0 6px 20px rgba(65, 105, 225, 0.4);
  transform: translateY(-2px);
}
