const bookingResults = $json.data || $json;

if (!Array.isArray(bookingResults)) {
  console.warn('Booking results is not an array, attempting to process as single item');
  const singleResult = bookingResults;
  if (singleResult && singleResult.url) {
    const cleanUrl = singleResult.url.split('?')[0];
    return [{ 
      startUrls: [{ url: cleanUrl }],
      originalCount: 1,
      cleanedCount: 1
    }];
  } else {
    return [{ 
      startUrls: [],
      originalCount: 0,
      cleanedCount: 0
    }];
  }
}

const cleanedUrls = bookingResults
  .filter(property => property && property.url)
  .map(property => {
    // Strip query parameters (everything after ?)
    const cleanUrl = property.url.split('?')[0];

    // Validate URL
    try {
      new URL(cleanUrl);
      return { url: cleanUrl };
    } catch (error) {
      console.error(`Invalid URL: ${property.url}`);
      return null;
    }
  })
  .filter(Boolean);

console.log(`Cleaned ${cleanedUrls.length} Booking URLs for review scraper from ${bookingResults.length} original results`);

return [{ 
  startUrls: cleanedUrls,
  originalCount: bookingResults.length,
  cleanedCount: cleanedUrls.length
}];

