// AIRBNB VALIDATION TOOL
// Validates parameters for Airbnb scraper API calls
// Used in N8N Code node for parameter validation before API requests

// Debug: Log all available parameters from AI Agent
console.log('=== AIRBNB VALIDATION DEBUG ===');
console.log('Available parameters from $fromAI:');
const availableParams = {};
const testParams = [
  'adults', 'check_in_date', 'check_out_date', 'children', 'currency',
  'infants', 'destination', 'min_bathrooms', 'min_bedrooms', 'min_beds',
  'pets', 'price_max', 'price_min', 'checkIn', 'checkOut', 'location',
  'locationQuery', 'locationQueries'
];

testParams.forEach(param => {
  try {
    const value = $fromAI(param);
    if (value !== undefined) {
      availableParams[param] = value;
      console.log(`‚úì ${param}: ${JSON.stringify(value)}`);
    } else {
      console.log(`‚úó ${param}: undefined`);
    }
  } catch (e) {
    console.log(`‚úó ${param}: ERROR - ${e.message}`);
  }
});

console.log('Available parameters object:', JSON.stringify(availableParams, null, 2));

// Extract parameters with multiple fallback options
const params = {
  adults: $fromAI('adults') || $fromAI('Adults') || 2,
  checkIn: $fromAI('check_in_date') || $fromAI('checkIn') || $fromAI('check_in'),
  checkOut: $fromAI('check_out_date') || $fromAI('checkOut') || $fromAI('check_out'),
  children: $fromAI('children') || $fromAI('Children') || 0,
  currency: $fromAI('currency') || $fromAI('Currency') || 'EUR',
  infants: $fromAI('infants') || $fromAI('Infants') || 0,
  locationQueries: $fromAI('locationQueries') || 
                   ($fromAI('destination') ? [$fromAI('destination')] : null) ||
                   ($fromAI('location') ? [$fromAI('location')] : null) ||
                   ($fromAI('locationQuery') ? [$fromAI('locationQuery')] : null),
  minBathrooms: $fromAI('min_bathrooms') || $fromAI('minBathrooms') || 1,
  minBedrooms: $fromAI('min_bedrooms') || $fromAI('minBedrooms') || 1,
  minBeds: $fromAI('min_beds') || $fromAI('minBeds') || 1,
  pets: $fromAI('pets') || $fromAI('Pets') || 0,
  priceMax: $fromAI('price_max') || $fromAI('priceMax') || $fromAI('maxPrice') || 999999,
  priceMin: $fromAI('price_min') || $fromAI('priceMin') || $fromAI('minPrice') || 0,
  locale: 'ro-RO'
};

console.log('Extracted parameters:', JSON.stringify(params, null, 2));

const errors = [];
const warnings = [];
const autoCorrections = [];

// Date format validation
const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
if (!params.checkIn || !dateRegex.test(params.checkIn)) {
  errors.push(`Invalid or missing checkIn: ${params.checkIn} (must be YYYY-MM-DD format)`);
}
if (!params.checkOut || !dateRegex.test(params.checkOut)) {
  errors.push(`Invalid or missing checkOut: ${params.checkOut} (must be YYYY-MM-DD format)`);
}

// Location validation with detailed error info
if (!params.locationQueries || !Array.isArray(params.locationQueries) || 
    params.locationQueries.length === 0 || !params.locationQueries[0] || 
    params.locationQueries[0].trim() === '') {
  errors.push(`Missing destination - locationQueries is ${JSON.stringify(params.locationQueries)}`);

  // Try to help debug by showing what destination-related parameters were available
  const destinationParams = {
    destination: $fromAI('destination'),
    location: $fromAI('location'),
    locationQuery: $fromAI('locationQuery'),
    locationQueries: $fromAI('locationQueries')
  };
  console.log('DEBUG - Destination parameters available:', JSON.stringify(destinationParams, null, 2));
}

// Price validation
if (params.priceMin > params.priceMax) {
  warnings.push(`priceMin (${params.priceMin}) > priceMax (${params.priceMax}) - may return no results`);
}

// Date logic validation
if (params.checkIn && params.checkOut && dateRegex.test(params.checkIn) && dateRegex.test(params.checkOut)) {
  const checkInDate = new Date(params.checkIn);
  const checkOutDate = new Date(params.checkOut);
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  if (checkInDate < today) {
    errors.push(`checkIn ${params.checkIn} is in the past`);
  }
  if (checkOutDate <= checkInDate) {
    errors.push(`checkOut ${params.checkOut} must be after checkIn ${params.checkIn}`);
  }
}

// Guest count validation
if (params.adults < 1) {
  autoCorrections.push(`Adults corrected from ${params.adults} to 1`);
  params.adults = 1;
}

// Log comprehensive validation results
const validationLog = {
  nodeType: 'airbnb',
  timestamp: new Date().toISOString(),
  extractedParams: params,
  availableFromAI: availableParams,
  errors,
  warnings,
  autoCorrections
};

console.log('=== AIRBNB VALIDATION RESULTS ===');
console.log(JSON.stringify(validationLog, null, 2));

if (errors.length > 0) {
  const errorMsg = `AIRBNB validation failed: ${errors.join(', ')}`;
  console.error(errorMsg);
  throw new Error(errorMsg);
}

if (warnings.length > 0) {
  console.warn('AIRBNB validation warnings:', warnings.join(', '));
}

if (autoCorrections.length > 0) {
  console.log('AIRBNB auto-corrections applied:', autoCorrections.join(', '));
}

console.log('‚úÖ AIRBNB validation passed');

return [{ 
  validatedParams: params,
  validationLog: validationLog
}];

// BOOKING.COM VALIDATION TOOL
// Validates parameters for Booking.com scraper API calls
// Used in N8N Code node for parameter validation before API requests

// Debug: Log all available parameters from AI Agent
